package learn_java_progs;

public class Arithmetic {

	// TODO Auto-generated constructor stub

	
	public static void main(String[] args) {
	 	int a1=2;
		int a2=2;
		int a3=2;
		int a4=2;
		int b;
		int c;
		int d;
		int e;
		
		b=a1++;
		c=++a2;
		d=a3--;
		e=--a4;
		System.out.println("a1 = " +a1 +"b = " +b);
		System.out.println("a2 = " +a2 +"b = " +c);
		System.out.println("a3 = " +a3 +"b = " +d);
		System.out.println("a4 = " +a4 +"b = " +e);
		
		
		int p=0;
		int q=2;
		
		q+=2; //q=q+2
		
		System.out.println("p = "+p+"q="+q);
		
	}
	
	
	
}
